// Fragment provides a container for jsx code to be rendered
import { Fragment, useState } from "react";
import classes from "./Body.module.css";
import ImageGallery from "./ImageGallery";

import Add from "../../images/icon-plus.svg";
import Minus from "../../images/icon-minus.svg";
import { ReactComponent as MyLogo } from "../../images/button-cart.svg";
import ImageModal from "../ImageModal/ImageModal";

const MainBody = () => {
  const [units, setUnits] = useState(0);
  const [isModal, setIsModal] = useState(null);

  const onDoneHandler = () => {
    setIsModal(!isModal);
  };
  const addUnit = () => {
    setUnits(units + 1);
  };
  const substractUnit = () => {
    if (units === 0) {
      return;
    }
    setUnits(units - 1);
  };

  return (
    <Fragment>
      {isModal && <ImageModal isOnModal={isModal} onDone={onDoneHandler} />}
      <div className={classes.main_div}>
        <ImageGallery onClick={onDoneHandler} />
        <Description></Description>
      </div>
    </Fragment>
  );
};

export default MainBody;
